def download_schema_file(envs: Dict[str, Any]) -> Path | NoReturn:
    schema_path = envs.get('SIGMA_SCHEMA_FILE', Path(envs['GITHUB_WORKSPACE']) / 'sigma-schema.json')
    schema_url = envs['SIGMA_SCHEMA_URL']

    if schema_path.exists():
        return schema_path.resolve()

    try:
        response = requests.get(schema_url)
        response.raise_for_status()
    except requests.RequestException as e:
        print(f"Failed to download schema file: {e}")
        exit(-1)

    schema_path.parent.mkdir(parents=True, exist_ok=True)
    with open(schema_path, 'wb') as f:
        f.write(response.content)

    return schema_path.resolve()
