import http.client
import queue
from urllib.parse import urlparse
from socket import timeout as SocketTimeout
from typing import Optional, Dict, Any

class Timeout:
    DEFAULT_TIMEOUT = 5

class Retry:
    DEFAULT_RETRIES = 3

class HTTPConnection(http.client.HTTPConnection):
    pass

class HTTPResponse(http.client.HTTPResponse):
    pass

class HTTPConnectionPool:
    def __init__(self, host: str, port: Optional[int] = None, strict: bool = False, timeout: Timeout = Timeout.DEFAULT_TIMEOUT, maxsize: int = 1, block: bool = False, headers: Optional[Dict[str, str]] = None, retries: Optional[Retry] = None, _proxy: Optional[str] = None, _proxy_headers: Optional[Dict[str, str]] = None, **conn_kw: Any) -> None:
        self.scheme = 'http'
        self.ConnectionCls = HTTPConnection
        self.ResponseCls = HTTPResponse
        self.host = host
        self.port = port
        self.timeout = timeout
        self.retries = retries
        self.pool = queue.LifoQueue(maxsize=maxsize)
        self.block = block
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers
        self.conn_kw = conn_kw

    def _new_conn(self) -> HTTPConnection:
        return self.ConnectionCls(host=self.host, port=self.port, **self.conn_kw)

    def _get_conn(self, timeout: Optional[float] = None) -> HTTPConnection:
        try:
            return self.pool.get(block=self.block, timeout=timeout)
        except queue.Empty:
            return self._new_conn()

    def _put_conn(self, conn: HTTPConnection) -> None:
        if not self.pool.full():
            self.pool.put(conn)
        else:
            conn.close()

    def _make_request(self, conn: HTTPConnection, method: str, url: str, timeout: float = Timeout.DEFAULT_TIMEOUT, **httplib_request_kw: Any) -> HTTPResponse:
        conn.request(method, url, **httplib_request_kw)
        return conn.getresponse()

    def urlopen(self, method: str, url: str, body: Optional[Any] = None, headers: Optional[Dict[str, str]] = None, retries: Optional[Retry] = None, redirect: bool = True, assert_same_host: bool = True, timeout: float = Timeout.DEFAULT_TIMEOUT, pool_timeout: Optional[float] = None, release_conn: Optional[bool] = None, **response_kw: Any) -> HTTPResponse:
        for attempt in range(retries or self.retries.DEFAULT_RETRIES + 1):
            try:
                conn = self._get_conn(pool_timeout)
                response = self._make_request(conn, method, url, timeout=timeout, **response_kw)
                if response.status == 302 and redirect:
                    location = response.getheader('Location')
                    parsed_url = urlparse(location)
                    if assert_same_host and parsed_url.netloc != self.host:
                        raise ValueError("Redirected to a different host")
                    return self.urlopen(method, location, body=body, headers=headers, retries=retries, redirect=redirect, assert_same_host=assert_same_host, timeout=timeout, pool_timeout=pool_timeout, release_conn=release_conn, **response_kw)
                self._put_conn(conn)
                return response
            except (SocketTimeout, http.client.BadStatusLine) as e:
                if attempt == retries or not isinstance(e, SocketTimeout):
                    raise
                continue
