import requests

class Config:
    ID = 'your_huawei_cloud_id'
    TOKEN = 'your_huawei_cloud_token'
    PROXY = None  # Set to your proxy if needed

class API:
    SCHEME = 'https'
    SITE = 'api.huaweicloud.com'

def request(method, path, param=None, body=None, **params):
    url = f"{API.SCHEME}://{API.SITE}{path}"
    headers = {
        'Content-Type': 'application/json',
        'X-Auth-Token': Config.TOKEN,
        'Timestamp': str(int(time.time()))
    }

    if param:
        url += '?' + '&'.join([f'{k}={v}' for k, v in param.items()])
    if params:
        url += '?' + '&'.join([f'{k}={v}' for k, v in params.items()])

    if isinstance(body, dict):
        body = json.dumps(body)

    message = f"{method}\n{url}\n{headers['Content-Type']}\n{body or ''}".encode('utf-8')
    signature = hmac.new(Config.ID.encode('utf-8'), message, hashlib.sha256).hexdigest()
    headers['Signature'] = signature

    proxies = {}
    if Config.PROXY:
        proxies = {'http': Config.PROXY, 'https': Config.PROXY}

    response = requests.request(method, url, headers=headers, data=body, proxies=proxies)

    if response.status_code >= 300:
        raise Exception(f"HTTP Error {response.status_code}: {response.text}")

    return response.json()
