from http.client import HTTPConnection, HTTPSConnection, HTTPResponse
from urllib.parse import urlparse
from socket import timeout as SocketTimeout
from time import sleep
from typing import Optional, Dict, Any

class Timeout:
    DEFAULT_TIMEOUT = 5.0

class Retry:
    DEFAULT_RETRIES = 3

class HTTPConnectionPool:
    def __init__(self, host: str, port: Optional[int] = None, timeout: float = Timeout.DEFAULT_TIMEOUT, maxsize: int = 1, block: bool = False, headers: Optional[Dict[str, str]] = None, retries: Optional[Retry] = None, _proxy: Optional[str] = None, _proxy_headers: Optional[Dict[str, str]] = None, **conn_kw: Any) -> None:
        self.scheme = "http" if port == 80 else "https"
        self.host = host
        self.port = port
        self.timeout = timeout
        self.maxsize = maxsize
        self.block = block
        self.headers = headers
        self.retries = retries or Retry()
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers
        self.conn_kw = conn_kw

    def _new_conn(self) -> HTTPConnection:
        if self.scheme == "http":
            return HTTPConnection(host=self.host, port=self.port, timeout=self.timeout, **self.conn_kw)
        else:
            return HTTPSConnection(host=self.host, port=self.port, timeout=self.timeout, **self.conn_kw)

    def _make_request(self, conn: HTTPConnection, method: str, url: str, timeout: Optional[float] = None, chunked: bool = False, **httplib_request_kw: Any) -> HTTPResponse:
        try:
            conn.request(method, url, headers=self.headers, **httplib_request_kw)
            return conn.getresponse()
        except SocketTimeout:
            raise TimeoutError("Request timed out")

