    def set_connection_timeout(self, connection_timeout):
        self.connection_timeout = connection_timeout
        self.conn = self._new_conn()

    def send_request(self, req_inter):
        try:
            self.conn.request(req_inter.method, req_inter.url, body=req_inter.body, headers=req_inter.headers)
            response = self.conn.getresponse()
            self.response_size = response.length
            return ResponseInternal(response.status, response.reason, response.getheaders(), response.read())
        except Exception as e:
            if self.logger:
                self.logger.error(f"Error sending request: {e}")
            raise

class MNSHTTPConnection(http.client.HTTPConnection):
    def __init__(self, *args, **kwargs):
        super().__init__(*args, **kwargs)
        self.request_length = 0

    def connect(self):
        if self.proxy:
            parsed_url = urlparse(self.proxy)
            self.sock = socket.create_connection((parsed_url.hostname, parsed_url.port))
            self.sock.settimeout(self.timeout)
            self._tunnel()
        else:
            self.sock = socket.create_connection((self.host, self.port))
            self.sock.settimeout(self.timeout)

    def send(self, data):
        self.request_length += len(data)
        super().send(data)

class MNSHTTPSConnection(http.client.HTTPSConnection):
    def __init__(self, *args, **kwargs):
        super().__init__(*args, **kwargs)
        self.request_length = 0

    def send(self, data):
        self.request_length += len(data)
        super().send(data)

class ResponseInternal:
    def __init__(self, status, reason, headers, body):
        self.status = status
        self.reason = reason
        self.headers = headers
        self.body = body
```
