        s.close()
        if data and len(data) > 1:
            ret = 1
    except socket.timeout:
        ret = 0
    except socket.error, serr:
        if serr[0] == errno.ECONNRESET:
            ret = -1
        elif serr[0] == errno.ETIMEDOUT:
            ret = 0
