                target = struct.unpack("f", byte_msg[1:])[0]
                response = b"0T"
            except Exception:
                response = b"FT'%s' cannot be converted to float." % (byte_msg[1])
        elif byte_msg[:1] == b"V":
            response = b"0V%s" % bytes(__version__)
        else:
            response = b'FFUnknown command. "%s"' % byte_msg

        socket.sendto(response, sender)

