import socket
from typing import Tuple, Optional

class UDPSocketHandler:
    IP_BINDANY = socket.INADDR_ANY
    IP_RECVDSTADDR = socket.IPPROTO_IP

    @staticmethod
    def recv_udp(listener: socket.socket, bufsize: int) -> Tuple[Tuple[str, int], Optional[Tuple[str, int]], bytes]:
        packet, addr = listener.recvmsg(bufsize, socket.CMSG_SPACE(socket.getaddrinfo('0.0.0.0', 0)[0][1]))
        srcip, srcport = addr[0][:2]
        cmsg_data = listener.getsockopt(socket.SOL_SOCKET, socket.SO_TIMESTAMPNS, 16)
        dstip, dstport = cmsg_data[8:].decode().split(':')
        return (srcip, int(srcport)), (dstip, int(dstport)) if dstip else None, packet

    @staticmethod
    def send_udp(sock: socket.socket, srcip: Tuple[str, int], dstip: Tuple[str, int], data: bytes) -> None:
        sock.sendto(data, src=srcip, dest=dstip)

    @staticmethod
