import socket
import struct

class UDPSocketHandler:
    def recv_udp(self, listener: socket.socket, bufsize: int) -> tuple:
        data, ancdata, flags, addr = listener.recvmsg(bufsize, socket.CMSG_LEN(16))
        for cmsg_level, cmsg_type, cmsg_data in ancdata:
            if cmsg_level == socket.SOL_IP and cmsg_type == socket.IP_RECVORIGDSTADDR:
                orig_dst_addr = struct.unpack('!I', cmsg_data[:4])[0]
                return (addr[0], addr[1]), (socket.inet_ntoa(struct.pack('!I', orig_dst_addr)), addr[1]), data
        return addr, addr, data

    def send_udp(self, sock: socket.socket, srcip: tuple, dstip: tuple, data: bytes) -> None:
        packet = socket.make_ipv4_pktinfo(srcip[0], srcip[1])
        sock.sendto(data, (dstip[0], dstip[1]), ancdata=[packet])

    def setup_udp_listener(self, udp_listener: object) -> None:
        udp_listener.v4.setsockopt(socket.IPPROTO_IP, socket.IP_TRANSPARENT, 1)
        udp_listener.v6.setsockopt(socket.IPPROTO_IPV6, socket.IPV6_TRANSPARENT, 1)

    def setsockopt_error(self, e: Exception) -> None:
        raise SystemExit(f"Fatal error: {e}")
