import requests

def gentle_request(wav_path, txt_path, server_addr, port, debug=False):
    if not wav_path.endswith('.wav') or not txt_path.endswith('.txt'):
        return None
    base_name = wav_path.rsplit('/', 1)[-1].rsplit('.', 1)[0]
    txt_base_name = txt_path.rsplit('/', 1)[-1].rsplit('.', 1)[0]
    if base_name != txt_base_name:
        return None
    try:
        with open(txt_path, 'r') as f:
            transcript = f.read()
    except Exception:
        return None
    files = {
        'audio': open(wav_path, 'rb'),
        'transcript': ('transcript.txt', transcript.encode('utf-8'))
    }
    try:
        response = requests.post(f'http://{server_addr}:{port}/process', files=files)
        if debug:
            print(response.json())
        return response
    except requests.exceptions.RequestException:
        return None
    finally:
        for file in files.values():
            if hasattr(file, 'close'):
                file.close()

