import requests
import openai

def get_embedding_from_api(word, model="vicuna-7b-v1.1"):
    try:
        if "ada" in model:
            response = openai.Embedding.create(input=word, model=model)
            embedding = np.array(response["data"][0]["embedding"])
            return embedding
        else:
            headers = {"Content-Type": "application/json"}
            data = {"input": word, "model": model}
            response = requests.post("http://localhost:5000/embedding", json=data, headers=headers)
            response.raise_for_status()
            embedding = np.array(response.json()["embedding"])
            return embedding
    except Exception as e:
        print(f"Error retrieving embedding: {e}")
        return None

