    def __init__(self):
        self.app = FastAPI(title="Citation with Extraction")
        self._setup_routes()

    def _setup_routes(self):
        @self.app.post("/extract", response_class=StreamingResponse)
        async def extract(question: Question, openai_key: str = Depends(self.get_api_key)):
            facts = self.stream_extract(question)
            async def generate():
                for fact in facts:
                    logger.info(f"Fact: {fact}")
                    spans = list(fact.get_spans(question.context))
                    resp = {
                        "body": fact.fact,
                        "spans": spans,
                        "citation": [question.context[a:b] for (a, b) in spans],
                    }
                    resp_json = json.dumps(resp)
                    yield f"data: {resp_json}"
                yield "data: [DONE]"
            return StreamingResponse(generate(), media_type="text/event-stream")

    def stream_extract(self, question: Question) -> Iterable[Fact]:
        completion = client.chat.completions.create(
            model="gpt-3.5-turbo-0613",
