        self.DIR_IMAGES = os.path.join(os.path.dirname(__file__), "images")
        self.DIR_JS = os.path.join(os.path.dirname(__file__), "js")

    class RequestHandler(BaseHTTPRequestHandler):
        def __init__(self, *args, **kwargs):
            self.DIR_SASS = kwargs.pop('DIR_SASS')
            self.DIR_IMAGES = kwargs.pop('DIR_IMAGES')
            self.DIR_JS = kwargs.pop('DIR_JS')
            super().__init__(*args, **kwargs)

        def log_message(self, format, *args):
            return

        def _set_headers(self, status, type):
            self.send_response(status)
            self.send_header("Content-type", type)
            self.end_headers()

        def do_GET(self):
            status = 200
            type = "application/json"
            data = None

            file = self.path.rpartition("/")[2]
            ext = file.rpartition(".")[2]

            if ext == "css":
                data = open(os.path.join(self.DIR_SASS, "PVEDiscordDark.css"), "rb").read()
                type = "text/css"
            elif ext == "js":
                data = open(os.path.join(self.DIR_JS, "PVEDiscordDark.js"), "rb").read()
                type = "application/javascript"
            elif ext in ("png", "jpg", "jpeg"):
                try:
                    data = open(os.path.join(self.DIR_IMAGES, file), "rb").read()
                    type = f"image/{ext}"
                except FileNotFoundError:
                    status = 404
            elif ext == "svg":
                try:
                    data = open(os.path.join(self.DIR_IMAGES, file), "rb").read()
                    type = f"image/svg+xml"
                except FileNotFoundError:
                    status = 404
            else:
                status = 400
            self._set_headers(status, type)
            if status == 200:
                self.wfile.write(data)
            else:
                self.wfile.write(json.dumps({"error": status}).encode())

    def run(self):
        print(f"Serving on localhost:{self.PORT}")
        server = HTTPServer(
            server_address=("", self.PORT),
            RequestHandlerClass=lambda *args, **kwargs: self.RequestHandler(
                *args,
                DIR_SASS=self.DIR_SASS,
                DIR_IMAGES=self.DIR_IMAGES,
                DIR_JS=self.DIR_JS,
                **kwargs
            )
        )
        try:
            server.serve_forever()
        except KeyboardInterrupt:
            quit()

