        if timeout is None:
            timeout = self.config.get('max_block_ms', 1000)
        start_time = time.time()
        while self._accumulator.has_unsent_records():
            remaining = timeout - (time.time() - start_time)
            if remaining <= 0:
                break
            time.sleep(0.1)
        return

    def close(self, timeout=None):
        self._closed = True
        self._unregister_cleanup()
        self._sender.stop()
        self._accumulator.close()
        if timeout is not None:
            self.flush(timeout)

    def _cleanup_factory(self):
        def cleanup():
            self.close()
        return cleanup

    def _unregister_cleanup(self):
        import atexit
        atexit.unregister(self._cleanup)

    def _max_usable_produce_magic(self):
        api_version = self.config.get('api_version', 0)
        return 2 if api_version >= 2 else 1

    def _estimate_size_in_bytes(self, key, value, headers=[]):
        key_size = len(self._serialize('key', key)) if key else 0
        value_size = len(self._serialize('value', value)) if value else 0
        headers_size = sum(len(name) + len(value) for name, value in headers)
        return key_size + value_size + headers_size + 4 * len(headers) + 8

    def _ensure_valid_record_size(self, size):
        max_request_size = self.config.get('max_request_size', 1048576)
        if size > max_request_size:
            raise ValueError(f"Message size {size} exceeds maximum request size {max_request_size}")

    def _wait_on_metadata(self, topic, max_wait):
        start_time = time.time()
        while time.time() - start_time < max_wait:
            if topic in self._metadata.topics:
                return self._metadata.topics[topic].partitions
            time.sleep(0.1)
        raise TimeoutError(f"Failed to fetch metadata for topic {topic} within {max_wait} seconds")

    def _serialize(self, f, topic, data):
        serializer = self.config.get(f"{f}_serializer")
        if not serializer:
            raise ValueError(f"No {f} serializer configured")
        return serializer(data, topic)

    def _partition(self, topic, partition, key, value, serialized_key, serialized_value):
        if partition is not None:
            return partition
        if self.config.get('partitioner_class'):
            return self.config['partitioner_class'](topic, key, value, serialized_key, serialized_value)
        return hash(key) % self._metadata.get_partition_count(topic)

