class Sender(threading.Thread):
    def __init__(self, accumulator, metadata, config):
        super().__init__()
        self.accumulator = accumulator
        self.metadata = metadata
        self.config = config
        self.running = True

    def run(self):
        while self.running:
            records = self.accumulator.buffers
            if not records:
                time.sleep(0.1)
                continue
            for topic, batch in records.items():
                partitions = self.metadata.get_partition_count(topic)
                for record, future in batch:
                    partition = self._partition(topic, record.partition, record.key, record.value)
                    self._send_message(topic, partition, record.key, record.value, future)

    def stop(self):
        self.running = False

    def _send_message(self, topic, partition, key, value, future):
        try:
            metadata = self._get_metadata(topic)
            broker = self._select_broker(metadata, partition)
            request = self._build_produce_request(topic, partition, key, value)
            response = self._send_to_broker(broker, request)
            future.set_result(response)
        except Exception as e:
            future.set_exception(e)

    def _get_metadata(self, topic):
        return self.metadata.topics.get(topic, [])

    def _select_broker(self, metadata, partition):
        return metadata[partition].leader

    def _build_produce_request(self, topic, partition, key, value):
        magic = self._max_usable_produce_magic()
        record = {
            'topic': topic,
            'partition': partition,
            'magic': magic,
            'key': key,
            'value': value
        }
        return ProduceRequest([record])

    def _send_to_broker(self, broker, request):
        sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        sock.connect((broker.host, broker.port))
        sock.sendall(request.serialize())
        response = sock.recv(4096)
        sock.close()
        return response

