class Retry:
    def __init__(self, total: int = 3, backoff_factor: float = 0.5, status_forcelist: list = [500, 502, 503, 504]):
        self.total = total
        self.backoff_factor = backoff_factor
        self.status_forcelist = status_forcelist

class HTTPConnectionPool:
    def __init__(self, host, port=None, strict=False, timeout=Timeout.DEFAULT_TIMEOUT, maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, **conn_kw):
        self.scheme = "http"
        self.host = host
        self.port = port or 80
        self.timeout = timeout
        self.maxsize = maxsize
        self.block = block
        self.headers = headers or {}
        self.retries = retries or Retry()
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers or {}
        self.conn_kw = conn_kw
        self.pool = deque()
        self._connect()

    def _connect(self):
        for _ in range(self.maxsize):
            conn = self._new_conn()
            self.pool.append(conn)

    def _new_conn(self) -> HTTPConnection:
        if self.proxy:
            proxy_url = urlparse(self.proxy)
            proxy_host = proxy_url.hostname
            proxy_port = proxy_url.port or 8080
            conn = HTTPConnection(proxy_host, proxy_port, **self.conn_kw)
            conn.set_tunnel(self.host, self.port, self.proxy_headers)
        else:
            conn = HTTPConnection(self.host, self.port, **self.conn_kw)
        return conn

    def _make_request(self, conn, method, url, timeout=Timeout.DEFAULT_TIMEOUT, chunked=False, **httplib_request_kw):
        try:
            conn.request(method, url, **httplib_request_kw)
            response = conn.getresponse()
            return response
        except Exception as e:
            raise HTTPException(f"Request failed: {e}")

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, assert_same_host=True, timeout=Timeout.DEFAULT_TIMEOUT, pool_timeout=None, release_conn=None, chunked=False, **response_kw):
        retries = retries or self.retries
        current_retry = 0
        while current_retry <= retries.total:
            try:
                conn = self._get_connection()
                request_headers = self.headers.copy()
                if headers:
                    request_headers.update(headers)
                httplib_request_kw = {
                    "body": body,
                    "headers": request_headers,
                    "chunked": chunked
                }
                response = self._make_request(conn, method, url, timeout, chunked, **httplib_request_kw)
                if redirect and response.status in [301, 302, 303, 307, 308]:
                    location = response.headers.get("Location")
                    if not location:
                        return response
                    parsed_url = urlparse(location)
                    if assert_same_host and parsed_url.hostname != self.host:
                        return response
                    url = location
                    continue
                return response
            except HTTPException as e:
                current_retry += 1
                if current_retry > retries.total:
                    raise e
                delay = retries.backoff_factor * (2 ** (current_retry - 1))
                time.sleep(delay)
        return None

    def _get_connection(self):
        if self.pool:
            return self.pool.popleft()
        else:
            if self.block:
                while not self.pool:
                    time.sleep(0.1)
                return self.pool.popleft()
            else:
                raise Exception("No available connections in the pool")

