from confluent_kafka import Producer

def create_kafka_producer(args):
    if isinstance(args, argparse.Namespace):
        config = {}
        for kv in args.consumer_config:
            key, value = kv.split('=', 1)
            try:
                config[key] = int(value)
            except ValueError:
                config[key] = value
        brokers = args.brokers
        record_size = args.record_size
        fixture_compression = args.fixture_compression
        num_records = args.num_records
        topic = args.topic
    else:
        config = args.get('config', {})
        brokers = args.get('brokers')
        record_size = args.get('record_size')
        fixture_compression = args.get('fixture_compression')
        num_records = args.get('num_records')
        topic = args.get('topic')

    if brokers:
        config['bootstrap_servers'] = ','.join(brokers)

    if 'compression_type' not in config:
        config['compression_type'] = fixture_compression

    print(f"Bootstrap servers: {config.get('bootstrap_servers', 'not set')}")

    producer = Producer(config)

    def delivery_report(err, msg):
        if err:
            print(f'Message delivery failed: {err}')
        else:
            print(f'Message delivered to {msg.topic()} [{msg.partition()}]')

    payload = os.urandom(record_size)
    for i in range(num_records):
        producer.produce(topic, key=str(i).encode('utf-8'), value=payload, callback=delivery_report)
        producer.poll(0)

    producer.flush()
    print(f"Sent {num_records} records of size {record_size} bytes to topic '{topic}'")

