import re

class SubprocessScanner:
    def __init__(self, host):
        self.host = host

    def useMasscanTCP(self):
        command = ['masscan', self.host, '-pT:1-65535', '--rate=1000']
        result = subprocess.run(command, stdout=subprocess.PIPE, stderr=subprocess.PIPE, text=True)
        ports = re.findall(r'(\d+)/tcp open', result.stdout)
        return list(map(int, ports))

    def useMasscanUDP(self):
        command = ['masscan', self.host, '-pU:1-65535', '--rate=1000']
        result = subprocess.run(command, stdout=subprocess.PIPE, stderr=subprocess.PIPE, text=True)
        ports = re.findall(r'(\d+)/udp open', result.stdout)
        return list(map(int, ports))

    def useNmapServTCP(self, port):
        command = ['nmap', '-sV', '-p', str(port), self.host]
        result = subprocess.run(command, stdout=subprocess.PIPE, stderr=subprocess.PIPE, text=True)
        service_info = re.search(r'([a-zA-Z0-9\-]+)\s+(\d+/\w+)\s+(\S+)', result.stdout)
        return service_info.group(3) if service_info else "Service not found"

    def useNmapServUDP(self, port):
        command = ['nmap', '-sU', '-sV', '-p', str(port), self.host]
        result = subprocess.run(command, stdout=subprocess.PIPE, stderr=subprocess.PIPE, text=True)
        service_info = re.search(r'([a-zA-Z0-9\-]+)\s+(\d+/\w+)\s+(\S+)', result.stdout)
        return service_info.group(3) if service_info else "Service not found"

