class DNSPodAPI:
    def __init__(self, token_id=None, token_key=None, proxy=None, ttl=None):
        self.SITE = "dnsapi.cn"
        self.METHOD = "POST"
        self.TOKEN_PARAM = "login_token"
        self.DEFAULT = "默认"
        self.LENGTH = "length"

        self.ID = token_id
        self.TOKEN = token_key
        self.PROXY = proxy
        self.TTL = ttl
        self.domain_list = {}
        self.records = {}
        self.record_keys = ("id", "name", "type", "line", "line_id", "enabled", "mx", "value")

    def request(self, action, param=None, **params):
        if param:
            params.update(param)
        params = {k: v for k, v in params.items() if v is not None}
        params.update({self.TOKEN_PARAM: "%s,%s" % (self.ID, self.TOKEN), 'format': 'json', self.LENGTH: "3000"})

        if self.PROXY:
            conn = HTTPSConnection(self.PROXY)
            conn.set_tunnel(self.SITE, 443)
        else:
            conn = HTTPSConnection(self.SITE)

        conn.request(self.METHOD, '/' + action, urlencode(params), {
            "Content-type": "application/x-www-form-urlencoded",
            "User-Agent": "DDNS/%s (ddns@newfuture.cc)" % environ.get("DDNS_VERSION", "1.0.0")
        })
        response = conn.getresponse()
        res = response.read().decode('utf8')
        conn.close()

        if response.status < 200 or response.status >= 300:
            raise Exception(res)
        else:
            data = jsondecode(res)
            if not data:
                raise Exception("empty response")
            elif data.get("status", {}).get("code") == "1":
                return data
            else:
                raise Exception(data.get('status', {}))

    def get_domain_info(self, domain):
        domain_split = domain.split('.')
        sub, did = None, None
        main = domain_split.pop()
        while domain_split:
            main = domain_split.pop() + '.' + main
            did = self.get_domain_id(main)
            if did:
                sub = ".".join(domain_split) or '@'
                break
        return did, sub

    def get_domain_id(self, domain):
        if domain in self.domain_list:
            return self.domain_list[domain]
        else:
            try:
                info = self.request('Domain.Info', domain=domain)
                did = info.get("domain", {}).get("id")
                if did:
                    self.domain_list[domain] = did
                    return did
            except Exception:
                return None

    def get_records(self, did, **conditions):
        if did not in self.records:
            self.records[did] = {}
            data = self.request('Record.List', domain_id=did)
            if data:
                for record in data.get('records'):
                    self.records[did][record["id"]] = {
                        k: v for (k, v) in record.items() if k in self.record_keys}

        records = {}
        for (rid, record) in self.records[did].items():
            for (k, value) in conditions.items():
                if record.get(k) != value:
                    break
            else:
                records[rid] = record
        return records

    def update_record(self, domain, value, record_type="A"):
        domainid, sub = self.get_domain_info(domain)
        if not domainid:
            raise Exception("invalid domain: [ %s ] " % domain)

        records = self.get_records(domainid, name=sub, type=record_type)
        result = {}
        if records:
            for (did, record) in records.items():
                if record["value"] != value:
                    res = self.request('Record.Modify', 
                                     record_id=did, 
                                     record_line=record["line"].replace("Default", "default").encode("utf-8"), 
                                     value=value, 
                                     sub_domain=sub, 
                                     domain_id=domainid, 
                                     record_type=record_type, 
                                     ttl=self.TTL)
                    if res:
                        self.records[domainid][did]["value"] = value
                        result[did] = res.get("record")
                    else:
                        result[did] = "update fail!\n" + str(res)
                else:
                    result[did] = domain
        else:
            res = self.request("Record.Create", 
                             domain_id=domainid, 
                             value=value,
                             sub_domain=sub, 
                             record_type=record_type, 
                             record_line=self.DEFAULT, 
                             ttl=self.TTL)
            if res:
                did = res.get("record")["id"]
                self.records[domainid][did] = res.get("record")
                self.records[domainid][did].update(
                    value=value, sub_domain=sub, record_type=record_type)
                result = res.get("record")
            else:
                result = domain + " created fail!"
        return result

