import re
import time

class ApcupsdCollector:
    def __init__(self, hostname='localhost', port=3551, metrics=None):
        self.config = {
            'hostname': hostname,
            'port': port,
            'metrics': metrics or ['LINEV', 'LOADPCT', 'BCHARGE', 'TIMELEFT', 'BATTV',
                                 'NUMXFERS', 'TONBATT', 'MAXLINEV', 'MINLINEV',
                                 'OUTPUTV', 'ITEMP', 'LINEFREQ', 'CUMONBATT']
        }

    def getData(self):
        s = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        s.connect((self.config['hostname'], int(self.config['port'])))
        s.send(pack('xb6s', 6, 'status'))
        s.recv(1024)
        time.sleep(.25)
        data = s.recv(4096)
        s.close()
        return data

    def collect(self):
        metrics = {}
        raw = {}
        data = self.getData()
        data = data.split('\n\x00')

        for d in data:
            matches = re.search("([A-Z]+)\s+:\s+(.*)$", d)
            if matches:
                value = matches.group(2).strip()
                raw[matches.group(1)] = value
                vmatch = re.search("([0-9.]+)", value)
                if not vmatch:
                    continue
                try:
                    value = float(vmatch.group(1))
                except ValueError:
                    continue
                metrics[matches.group(1)] = value

        results = {}
        for metric in self.config['metrics']:
            if metric not in metrics:
                continue
            metric_name = "%s.%s" % (raw['UPSNAME'], metric)
            value = metrics[metric]
            results[metric_name] = value

        return results

