        try:
            socket_connection = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
            socket_connection.settimeout(timeout)
            socket_connection.connect((host, port))
            ssl_flag = False
        except ConnectionRefusedError:
            return None

