        self.sock_list = []
        for ip, mask in self.ip_list:
            try:
                mreq = socket.inet_aton(SSDP_ADDR) + socket.inet_aton(ip)
                self.sock.setsockopt(socket.IPPROTO_IP, socket.IP_ADD_MEMBERSHIP, mreq)
                self.sock_list.append(Sock(ip))
            except Exception:
                pass

        try:
            self.sock.bind(('0.0.0.0', SSDP_PORT))
        except Exception:
            return
        self.sock.settimeout(1)

        while self.running:
            try:
                data, addr = self.sock.recvfrom(1024)
                self.datagram_received(data, addr)
            except socket.timeout:
                continue

        self.shutdown()
        for ip, mask in self.ip_list:
            mreq = socket.inet_aton(SSDP_ADDR) + socket.inet_aton(ip)
            try:
                self.sock.setsockopt(socket.IPPROTO_IP, socket.IP_DROP_MEMBERSHIP, mreq)
            except Exception:
                continue
        self.sock.close()
        self.sock = None

    def datagram_received(self, data, host_port):
        try:
            header = data.decode().split('\r\n\r\n')[0]
        except ValueError:
            return
        if len(header) == 0:
            return

        lines = header.split('\r\n')
        cmd = lines[0].split(' ')
        lines = map(lambda x: x.replace(': ', ':', 1), lines[1:])
        lines = filter(lambda x: len(x) > 0, lines)
        headers = dict(map(lambda x: (x[0].lower(), x[1]), [x.split(':', 1) for x in lines]))

        if cmd[0] == 'M-SEARCH' and cmd[1] == '*':
            self.discovery_request(headers, host_port)

    def discovery_request(self, headers, host_port):
        (host, port) = host_port
        for i in self.known.values():
            if i['ST'] == headers['st'] or headers['st'] == 'ssdp:all':
                response = ['HTTP/1.1 200 OK']
                usn = None
                for k, v in i.items():
                    if k == 'USN':
                        usn = v
                    response.append('%s: %s' % (k, v))

                if usn:
                    response.append('DATE: %s' % formatdate(timeval=None, localtime=False, usegmt=True))
                    response.extend(('', ''))
                    destination = (host, port)
                    for ip, mask in self.ip_list:
                        if self.get_subnet_ip(ip, mask) == self.get_subnet_ip(host, mask):
                            self.sock.sendto('\r\n'.join(response).format(ip).encode(), destination)
                            break

    def get_subnet_ip(self, ip, mask):
        a = [int(n) for n in mask.split('.')]
        b = [int(n) for n in ip.split('.')]
        return [a[i] & b[i] for i in range(4)]

    def shutdown(self):
        for st in self.known:
            self.do_byebye(st)
        usn = [st for st in self.known]
        for st in usn:
            self.unregister(st)

    def register(self, usn, st, location, server=SERVER_ID, cache_control='max-age=1800'):
        self.known[usn] = {
            'USN': usn,
            'LOCATION': location,
            'ST': st,
            'EXT': '',
            'SERVER': server,
            'CACHE-CONTROL': cache_control
        }

    def unregister(self, usn):
        del self.known[usn]

    def do_byebye(self, usn):
        if not self.sending_byebye:
            return

        resp = [
            'NOTIFY * HTTP/1.1',
            'HOST: %s:%d' % (SSDP_ADDR, SSDP_PORT),
            'NTS: ssdp:byebye',
        ]
        try:
            stcpy = dict(self.known[usn].items())
            stcpy['NT'] = stcpy['ST']
            del stcpy['ST']
            resp.extend(map(lambda x: ': '.join(x), stcpy.items()))
            resp.extend(('', ''))
            if self.sock:
                try:
                    self.send_it('\r\n'.join(resp), (SSDP_ADDR, SSDP_PORT))
                except (AttributeError, socket.error):
                    pass
        except KeyError:
            pass

    def send_it(self, response, destination):
        for sock in self.sock_list:
            sock.send_it(response, destination)

