def request(action, param=None, **params):
    if param is None:
        param = {}
    param.update(params)
    url = f"https://api.example.com/api/{action}"

    # Sign the parameters
    sorted_params = sorted(param.items())
    param_str = urlencode(sorted_params)
    signature = hmac.new(b'secret_key', msg=param_str.encode(), digestmod=hashlib.sha256).hexdigest()
    param['signature'] = signature

    headers = {
        'Content-Type': 'application/x-www-form-urlencoded',
        'User-Agent': 'CustomClient/1.0'
    }

