def gentle_request(wav_path, txt_path, server_addr, port, debug=False):
    wav_basename = os.path.splitext(os.path.basename(wav_path))[0]
    txt_basename = os.path.splitext(os.path.basename(txt_path))[0]
    if wav_basename != txt_basename:
        return None

    try:
        with open(txt_path, 'r') as f:
            transcript = f.read()
    except Exception:
        return None

    try:
        with open(wav_path, 'rb') as wav_file, open(txt_path, 'rb') as txt_file:
            files = {
                'audio': (os.path.basename(wav_path), wav_file),
                'transcript': (os.path.basename(txt_path), txt_file)
            }
            url = f'http://{server_addr}:{port}/'
            response = requests.post(url, files=files)
    except Exception:
        return None

    if 200 <= response.status_code < 300:
        if debug:
            print(response.json())
        return response
    else:
        return None

