import requests
import urllib.parse

class FileOnlineDelete_gitee:
    def __init__(self):
        self.url_prefix = "https://gitee.com/api/v5"
        self.show_help = ""

    def delete_file_from_branch(self, repo_owner: str, repo_name: str, file_path: str, branch: str, access_token: str, commit_message: str = "[comfyui_LLM_party] file deleted") -> tuple:
        encoded_file_path = urllib.parse.quote(file_path)
        get_url = f"{self.url_prefix}/repos/{repo_owner}/{repo_name}/contents/{encoded_file_path}?access_token={access_token}&ref={branch}"
        get_response = requests.get(get_url)
        if get_response.status_code != 200:
            return (f"Failed to retrieve file info: {get_response.text}",)
        file_data = get_response.json()
        sha = file_data.get('sha')
        if not sha:
            return ("File not found or SHA not available",)
        post_url = get_url
        post_data = {
            "message": commit_message,
            "branch": branch,
            "sha": sha,
            "action": "delete"
        }
        post_headers = {"Content-Type": "application/json"}
        post_response = requests.post(post_url, headers=post_headers, json=post_data)
        if post_response.status_code == 200:
            return ("Success",)
        else:
            return (f"Failed to delete file: {post_response.text}",)

