        def results():
            review = request.args.get('review')
            if not review:
                return redirect(url_for('index'))
            sentiment, prob = self.classify(review)
            return render_template('results.html', review=review, sentiment=sentiment, probability=prob)

        @self.app.route('/feedback', methods=['POST'])
        def feedback():
            review = request.form['review']
            y = 1 if request.form['sentiment'] == 'positive' else 0
            self.train(review, y)
            self.sqlite_entry(review, y)
            return redirect(url_for('index'))

