        self.clf.partial_fit(X, [y], classes=[0, 1])

    def sqlite_entry(self, document, y):
        with sqlite3.connect(self.db) as conn:
            conn.execute("INSERT INTO reviews (review, sentiment) VALUES (?, ?)", (document, y))

    def run(self):
        self.app.run(debug=True)

