        @self.app.route('/ingest', methods=['GET'])
        def ingest_data():
            source_dir = "documents"
            docs = self._load_documents(source_dir)
            text_splitter = RecursiveCharacterTextSplitter(chunk_size=1000, chunk_overlap=200)
            splits = text_splitter.split_documents(docs)
            embeddings = HuggingFaceInstructEmbeddings(model_name=self.embeddings_model_name)
            db = FAISS.from_documents(splits, embeddings)
            db.save_local(self.persist_directory)
            return jsonify({"status": "Documents ingested and vectorstore created"})

