        def get_answer():
            data = request.json
            question = data.get('question')
            embeddings = HuggingFaceInstructEmbeddings(model_name=self.embeddings_model_name)
            db = FAISS.load_local(self.persist_directory, embeddings, allow_dangerous_deserialization=True)
            qa_chain = RetrievalQA.from_chain_type(self.llm, retriever=db.as_retriever())
            answer = qa_chain.run(question)
            return jsonify({"answer": answer})

        @self.app.route('/download_model', methods=['GET'])
        def download_and_save():
            model_url = "https://example.com/models/gpt4all-lora-quantized-128.gguf"
            if not os.path.exists(self.model_path):
                response = requests.get(model_url)
                with open(self.model_path, 'wb') as f:
                    f.write(response.content)
            return jsonify({"status": "Model downloaded and saved"})

    def _load_documents(self, source_dir):
        docs = []
        for filename in os.listdir(source_dir):
            file_path = os.path.join(source_dir, filename)
            if filename.endswith(".pdf"):
                loader = PyPDFLoader(file_path)
            elif filename.endswith(".txt"):
                loader = TextLoader(file_path)
            else:
                continue
            docs.extend(loader.load())
        return docs

