        self.app.route('/users', methods=['POST'])(self.create_user)
        self.app.route('/users/<int:user_id>', methods=['PUT'])(self.update_user)
        self.app.route('/users/<int:user_id>', methods=['DELETE'])(self.delete_user)
        self.app.route('/orders/<order_id>', methods=['DELETE'])(self.delete_order)
        self.app.route('/spec', methods=['GET'])(self.get_spec)

    def get_spec(self):
        spec = {
            "openapi": "3.0.0",
            "info": {"title": "User Management API", "version": "1.0.0"},
            "paths": {
                "/users": {
                    "get": {"summary": "List users"},
                    "post": {"summary": "Create user"}
                },
                "/users/{user_id}": {
                    "get": {"summary": "Get user"},
                    "put": {"summary": "Update user"},
                    "delete": {"summary": "Delete user"}
                },
                "/orders/{order_id}": {
                    "delete": {"summary": "Delete order"}
                }
            }
        }
        return jsonify(spec)

    def get_user(self, user_id):
        if user_id not in self.users:
            abort(404)
        return jsonify(self.users[user_id])

    def list_users(self):
        return jsonify(list(self.users.values()))

    def create_user(self):
        data = request.get_json()
        if not data or 'name' not in data:
            abort(400)
        user = {
            'id': self.next_user_id,
            'name': data['name'],
            'email': data.get('email', '')
        }
        self.users[self.next_user_id] = user
        self.next_user_id += 1
        return jsonify(user), 201

    def update_user(self, user_id):
        if user_id not in self.users:
            abort(404)
        data = request.get_json()
        if not data:
            abort(400)
        self.users[user_id].update(data)
        return jsonify(self.users[user_id])

    def delete_user(self, user_id):
        if user_id not in self.users:
            abort(404)
        del self.users[user_id]
        return '', 204

    def delete_order(self, order_id):
        self.deleted_orders.add(order_id)
        return '', 204

    def check_auth(self):
        if self.config.auth_required and not request.headers.get('Authorization'):
            abort(401)

