    def query_db(self, query, args=(), one=False):
        cur = self.app.g.db.execute(query, args)
        rv = cur.fetchall()
        cur.close()
        return (rv[0] if rv else None) if one else rv

    def get_user_id(self, username):
        user = self.query_db('SELECT id FROM users WHERE username = ?', (username,), one=True)
        return user[0] if user else None

    def papers_filter_version(self, papers, v):
        return [p for p in papers if p['version'] == v]

    def default_context(self, papers, **kws):
        return {'papers': papers, **kws}

    def run(self, port=5000, debug=False, prod=False):
        if prod:
            self.app.run(host='0.0.0.0', port=port, debug=debug)
        else:
            self.app.run(port=port, debug=debug)

