                    timeout_val = timeout if timeout is not _Default else self.timeout
                    response = self._make_request(conn, method, url, timeout=timeout_val, chunked=chunked)
                    return response
                except socket.timeout:
                    if current_retries < retries.total:
                        current_retries += 1
                        time.sleep(retries.backoff_factor * (2 ** current_retries))
                        continue
                    raise
            finally:
                if release_conn is not None:
                    if release_conn:
                        self.pool.put(conn)
                    else:
                        conn.close()
        raise Exception("Max retries exceeded")

