class FlaskAPI:
    def __init__(self):
        self.app = Flask(__name__)
        self._setup_routes()
        self.server_thread = None

    def _setup_routes(self):
        def status():
            return jsonify({"status": "ok"})

        def handle_input():
            data = request.get_json()
            text = data.get('text')
            screen = data.get('screen')
            talk = data.get('talk')
            result = self._the_input(text, screen, talk)
            return jsonify(result)

        def shutdown():
            if self.server_thread:
                self.server_thread.shutdown()
            return jsonify({"status": "shutdown initiated"})

        self.app.add_url_rule('/status', 'status', status, methods=['GET'])
        self.app.add_url_rule('/input', 'input', handle_input, methods=['POST'])
        self.app.add_url_rule('/shutdown', 'shutdown', shutdown, methods=['POST'])

    def _the_input(self, text: str, screen: str, talk: str) -> dict:
        return {
            "text_received": text,
            "screen_received": screen,
            "talk_received": talk
        }

    def start_api(self, api: bool = False):
        if not api:
            self.server_thread = self.ServerThread(self.app, '0.0.0.0', 5000)
            self.server_thread.start()
        else:
            serve(self.app, host='0.0.0.0', port=5000)

    def stop_api(self):
        if self.server_thread:
            self.server_thread.shutdown()
            self.server_thread.join()

    class ServerThread(threading.Thread):
        def __init__(self, app: Flask, host: str, port: int):
            super().__init__()
            self.app = app
            self.host = host
            self.port = port
            self.srv = None
            self.ctx = app.app_context()

        def run(self):
            self.ctx.push()
            self.srv = make_server(self.host, self.port, self.app)
            try:
                self.srv.serve_forever()
            finally:
                self.ctx.pop()

        def shutdown(self):
            if self.srv:
                self.srv.shutdown()

