import json

def trace(files=None, port=9999, host='127.0.0.1', browser=False, daemon=False):
    counts = defaultdict(lambda: defaultdict(int))
    stack = []
    lock = threading.Lock()

    app = Flask(__name__)

    @app.route('/')
    def index():
        return '''
            <html>
                <head><title>Code Trace</title></head>
                <body>
                    <h1>Code Execution Trace</h1>
                    <div id="heatmap"></div>
                    <script>
                        function fetchData() {
                            fetch('/data')
                                .then(response => response.json())
                                .then(data => {
                                    console.log("Heatmap Data:", data);
                                });
                            setTimeout(fetchData, 1000);
                        }
                        fetchData();
                    </script>
                </body>
            </html>
        '''

    @app.route('/data')
    def get_data():
        with lock:
            data = {k: dict(v) for k, v in counts.items()}
        return jsonify(data)

    @app.route('/stack')
    def get_stack():
        with lock:
            stack_data = [f"{frame.f_code.co_filename}:{frame.f_lineno}" for frame in stack]
        return jsonify(stack_data)

    def run_server():
        app.run(host=host, port=port, debug=True)

    thread = threading.Thread(target=run_server, daemon=daemon)
    thread.start()

    def trace_func(frame, event, arg):
        if event == 'line':
            filename = frame.f_code.co_filename
            if files and not files(filename):
                return
            line = frame.f_lineno
            with lock:
                counts[filename][line] += 1
        elif event == 'call':
            with lock:
                stack.append(frame)
        elif event == 'return':
            with lock:
                if stack:
                    stack.pop()
        return trace_func

    sys.settrace(trace_func)

    if browser:
        webbrowser.open(f'http://{host}:{port}')

    while True:
        pass

