        def apitts():
            text = request.form.get('text')
            language = request.form.get('language')
            voice = request.form.get('voice')
            if not text or not language or not voice:
                return jsonify({'code': 1, 'msg': 'Missing parameters'})
            try:
                audio_path = self.generate_tts(text, voice, language)
                url = f'/audio/{os.path.basename(audio_path)}'
                return jsonify({'code': 0, 'url': url})
