        def ttslist():
            voice = request.form.get('voice')
            src = request.form.get('src')
            dst = request.form.get('dst')
            speed = request.form.get('speed')
            language = request.form.get('language')
            if not all([voice, src, dst, speed, language]):
                return jsonify({'code': 1, 'msg': 'Missing parameters'})
            try:
                speed = float(speed)
                thread = threading.Thread(target=self.detail_task, args=(voice, src, dst, speed, language))
                thread.start()
                return jsonify({'code': 0, 'msg': 'ok'})
            except Exception as e:
                self.app.logger.error(f"Batch processing initiation failed: {e}")
                return jsonify({'code': 2, 'msg': str(e)})

        @self.app.route('/ttslistjindu', methods=['GET'])
        def ttslistjindu():
            return jsonify(self.chuliing)

        @self.app.route('/audio/<filename>')
        def serve_audio(filename):
            return send_from_directory('static/audio', filename)

    def generate_tts(self, text, voice, language):
        import time
        time.sleep(1)
        audio_dir = os.path.join('static', 'audio')
        os.makedirs(audio_dir, exist_ok=True)
        audio_file = os.path.join(audio_dir, f"{voice}_{language}.mp3")
        with open(audio_file, 'w') as f:
            f.write("Generated audio content")
        return audio_file

    def detail_task(self, voice, src, dst, speed, language):
        self.chuliing['name'] = ''
        self.chuliing['line'] = 0
        self.chuliing['end'] = False
        try:
            files = [f for f in os.listdir(src) if f.endswith('.txt')]
            for idx, filename in enumerate(files):
                self.chuliing['name'] = filename
                file_path = os.path.join(src, filename)
                with open(file_path, 'r') as f:
                    lines = f.readlines()
                for line_num, line in enumerate(lines):
                    self.chuliing['line'] = line_num + 1
                    audio_path = self.generate_tts(line, voice, language)
                    dst_path = os.path.join(dst, f"{os.path.splitext(filename)[0]}_{line_num+1}.mp3")
                    os.rename(audio_path, dst_path)
                self.chuliing['line'] = 0
            self.chuliing['end'] = True
        except Exception as e:
            self.app.logger.error(f"Batch processing error: {e}")
            self.chuliing['end'] = True

    def run(self, host='0.0.0.0', port=5000):
        self.app.run(host=host, port=port)

