                + "".join(random.choice(string.ascii_lowercase) for _ in range(10))
            )
            return Response(
                json_object,
                mimetype="application/json",
                headers={"Content-Disposition": "attachment;filename=" + filename + ".json"},
            )

    def _sanitize_report_path_filename(self, report_path_filename):
        filename = secure_filename(os.path.basename(report_path_filename))
        if not filename:
            return False
        VALID_EXTENSIONS = (".html", ".htm", ".txt", ".json", ".csv")
        if "." in filename:
            if filename.endswith(VALID_EXTENSIONS):
                safe_report_path = nettacker_path_config.results_dir / filename
            else:
                return False
        else:
            safe_report_path = nettacker_path_config.results_dir / filename
        if not safe_report_path.is_relative_to(nettacker_path_config.results_dir):
            return False
        return safe_report_path

    def run(self):
        if self.options.api_cert and self.options.api_cert_key:
            self.app.run(
                host=self.options.api_hostname,
                port=self.options.api_port,
                debug=self.options.api_debug_mode,
                ssl_context=(self.options.api_cert, self.options.api_cert_key),
                threaded=True,
            )
        else:
            self.app.run(
                host=self.options.api_hostname,
                port=self.options.api_port,
                debug=self.options.api_debug_mode,
                ssl_context="adhoc",
                threaded=True,
            )

    def start_api_server(self):
        p = multiprocessing.Process(target=self.run)
        p.start()
        while len(multiprocessing.active_children()) != 0:
            try:
                time.sleep(0.3)
            except KeyboardInterrupt:
                for process in multiprocessing.active_children():
                    process.terminate()
                break

