                return jsonify({"error": "Unauthorized"}), 401

            data = request.json
            if not data or 'target' not in data or 'scan_type' not in data:
                return jsonify({"error": "Missing required parameters"}), 400

            return jsonify({
                "status": "scan initiated",
                "arguments": data
            })

        @self.app.route('/results/<scan_id>.json', methods=['GET'])
        def get_results_json(scan_id):
            if request.headers.get('secret') != self.app.config['OWASP_NETTACKER_CONFIG']['api_secret']:
                return jsonify({"error": "Unauthorized"}), 401

            results_path = Path(self.app.config['OWASP_NETTACKER_CONFIG']['results_dir']) / f"{scan_id}.json"
            if not self._sanitize_report_path_filename(results_path):
                return jsonify({"error": "Invalid file path"}), 400

            if not results_path.exists():
                return jsonify({"error": "File not found"}), 404

            return send_file(str(results_path), mimetype='application/json')

        @self.app.route('/logs/<scan_id>.json', methods=['GET'])
        def get_logs(scan_id):
            if request.headers.get('secret') != self.app.config['OWASP_NETTACKER_CONFIG']['api_secret']:
