            if not self._sanitize_report_path_filename(logs_path):
                return jsonify({"error": "Invalid file path"}), 400

            if not logs_path.exists():
                return jsonify({"error": "File not found"}), 404

            return send_file(str(logs_path), mimetype='application/json')

    def _sanitize_report_path_filename(self, report_path_filename):
        try:
            resolved_path = report_path_filename.resolve()
            base_dir = Path(self.app.config['OWASP_NETTACKER_CONFIG']['results_dir']).resolve()
            if resolved_path.is_relative_to(base_dir):
                return resolved_path
            return False
        except Exception:
            return False

    def run(self):
        host = self.app.config['OWASP_NETTACKER_CONFIG']['host']
        port = self.app.config['OWASP_NETTACKER_CONFIG']['port']
        ssl_cert = self.app.config['OWASP_NETTACKER_CONFIG']['ssl_cert']
        ssl_key = self.app.config['OWASP_NETTACKER_CONFIG']['ssl_key']

        self.app.run(
            host=host,
            port=port,
            ssl_context=(ssl_cert, ssl_key) if ssl_cert and ssl_key else None
        )

    def start_api_server(self):
        server_process = multiprocessing.Process(target=self.run)
        server_process.start()
        return server_process

