from flask import Flask, request, Response, Blueprint, jsonify
from flask_cors import CORS
import logging
import sys

class FlaskWithExceptionFormatting(Flask):
    def __init__(self, *args, **kwargs):
        super().__init__(*args, **kwargs)
        self._register_error_handlers()

    def _register_error_handlers(self):
        @self.app.errorhandler(Exception)
        def handle_exception(e):
            response = {
                'error': str(e),
                'traceback': ''.join(logging.formatException(e))
            }
            return jsonify(response), 500

class Server:
    def __init__(self, data=None, formats=None, authorization=None, allow_profiler=False, profiler_output=None, profile_by_default=False, allow_add=False, logfile=sys.stdout, loglevel='WARNING', log_exception_formatter=None):
        self.app = FlaskWithExceptionFormatting(__name__)
        self.data = data or {}
        self.port = 6363
        self.formats = formats or ['json']
        self.authorization = authorization
        self.allow_profiler = allow_profiler
        self.profiler_output = profiler_output
        self.profile_by_default = profile_by_default
        self.allow_add = allow_add

        self._setup_logging(logfile, loglevel, log_exception_formatter)
        self._setup_cors()
        self._setup_blueprints()

    def _setup_logging(self, logfile, loglevel, log_exception_formatter):
        logging.basicConfig(
            level=loglevel,
            stream=logfile,
            format='%(asctime)s - %(levelname)s - %(message)s'
        )

    def _setup_cors(self):
        CORS(self.app)

    def _setup_blueprints(self):
        api = Blueprint('api', __name__)

        @api.route('/compserver', methods=['POST', 'HEAD', 'OPTIONS'])
        def compserver():
            if request.method == 'OPTIONS':
                return '', 204

            if request.method == 'HEAD':
                return '', 200

            try:
                content_type = request.headers.get('Content-Type')
                if content_type not in self.formats:
                    return jsonify({'error': 'Unsupported format'}), 415

                data = request.get_json() if 'json' in content_type else {}
                result = self._compute(data)

                if 'json' in request.accept_mimetypes:
                    return jsonify(result)
                return Response(str(result), mimetype='text/plain')

            except Exception as e:
                self.app.logger.error(f"Exception: {e}", exc_info=True)
                return jsonify({'error': str(e)}), 500

        self.app.register_blueprint(api)

    def _compute(self, data):
        return {'result': 'computed', 'input': data}

    def run(self, port=6363, retry=False, **kwargs):
        self.port = port
        self.app.run(host='0.0.0.0', port=port, **kwargs)

