            context.verify_mode = ssl.CERT_NONE
        else:
            context = ssl.create_default_context()
        return context

    def _get_proxy_config(self):
        proxy_env = os.environ.get('HTTP_PROXY') or os.environ.get('HTTPS_PROXY')
        if proxy_env:
            proxy_url = proxy_env.split('://')[1]
            proxy_host, proxy_port = proxy_url.split(':', 1)
            return proxy_host, int(proxy_port)
        return None, None

    def _connect(self):
        proxy_host, proxy_port = self._get_proxy_config()
        if proxy_host and proxy_port:
            if self.ssl_enabled:
                self._conn = http.client.HTTPSConnection(proxy_host, proxy_port)
            else:
                self._conn = http.client.HTTPConnection(proxy_host, proxy_port)
            self._conn.putrequest('POST', self._url)
            self._conn.putheader('Host', f"{self._server}:{self._port}")
            for header, value in self._headers.items():
                self._conn.putheader(header, value)
            self._conn.putheader('Transfer-Encoding', 'chunked')
            self._conn.endheaders()
        else:
            if self.ssl_enabled:
                context = self._get_ssl_context()
                self._conn = http.client.HTTPSConnection(self._server, self._port, context=context)
            else:
                self._conn = http.client.HTTPConnection(self._server, self._port)
            self._conn.putrequest('POST', self._url)
            for header, value in self._headers.items():
                self._conn.putheader(header, value)
            self._conn.putheader('Transfer-Encoding', 'chunked')
            self._conn.endheaders()

    def write(self, data, reconnect_on=("", 200, 502)):
        try:
            chunk_size = len(data)
            chunk_header = f"{chunk_size:x}\r\n".encode('ascii')
            chunk_data = data + b'\r\n'
            self._conn.send(chunk_header + chunk_data)
        except Exception:
            self._reconnect()

    def close(self):
        try:
            self._conn.send(b'0\r\n\r\n')
            response = self._getresponse()
            body = response.read()
            self._closed = True
            return body
        except Exception:
            self._reconnect()
            return b''

    def _getresponse(self):
        try:
            return self._conn.getresponse()
        except Exception:
            self._reconnect()
            return self._conn.getresponse()

    def _isconnected(self):
        return not self._closed and self._conn is not None

    def _reconnect(self):
        if self._tries >= self.maxtries:
            self._closed = True
            return
        self._tries += 1
        delay = self._delay * (2 ** self._tries)
        time.sleep(delay)
        self._connect()

