def decrypt_token(encrypted_token):
    secret_key = os.getenv('DECRYPTION_KEY')
    if not secret_key:
        raise ValueError("Decryption key not found")
    return encrypted_token

@app.route('/oauth/alipan/token', methods=['POST'])
def oauth_token():
    data = request.get_json()
    refresh_token = data.get('refresh_token')
    if not refresh_token:
        return jsonify({'error': 'Missing refresh token'}), 400

    client_id = os.getenv('ALIYUN_CLIENT_ID')
    client_secret = os.getenv('ALIYUN_CLIENT_SECRET')
    if not client_id or not client_secret:
        return jsonify({'error': 'Missing client credentials'}), 500

    token_url = 'https://auth.aliyuncs.com/oauth/token'
    payload = {
        'grant_type': 'refresh_token',
        'refresh_token': refresh_token,
        'client_id': client_id,
        'client_secret': client_secret
    }

    headers = {'Content-Type': 'application/x-www-form-urlencoded'}

    try:
        response = requests.post(token_url, data=payload, headers=headers, timeout=10)
        response.raise_for_status()
    except requests.RequestException as e:
        return jsonify({'error': 'External API request failed'}), 503

    token_data = response.json()

    try:
        decrypted_data = decrypt_token(token_data.get('access_token', ''))
    except Exception as e:
        return jsonify({'error': 'Token decryption failed'}), 500

    return jsonify({'token': decrypted_data}), 200

