# Agent state
inventory = {}
queue = []
balance = 10000.0

@app.route('/')
def index():
    return jsonify({'status': 'OK'})

@app.route('/inventory')
def get_inventory():
    return jsonify(inventory)

@app.route('/queue')
def get_queue():
    return jsonify(queue)

@app.route('/balance')
def get_balance():
    return jsonify({'capital': balance})

@app.route('/trade')
def execute_trade():
    data_str = request.args.get('data')
    if not data_str:
        return jsonify({'error': 'Missing trade data'}), 400

    try:
        trade_data = jsonify(data_str).get_json()
    except:
        return jsonify({'error': 'Invalid trade data format'}), 400

    symbol = trade_data.get('symbol')
    amount = trade_data.get('amount')
    price = trade_data.get('price')

    if not all([symbol, amount, price]):
        return jsonify({'error': 'Incomplete trade data'}), 400

    cost = amount * price
    if balance < cost:
        return jsonify({'error': 'Insufficient capital'}), 400

    balance -= cost
    inventory[symbol] = inventory.get(symbol, 0) + amount
    queue.append({
        'symbol': symbol,
        'amount': amount,
        'price': price,
        'status': 'executed'
    })

    return jsonify({
        'symbol': symbol,
        'amount': amount,
        'price': price,
        'status': 'success'
    })

@app.route('/reset')
def reset_capital():
    money_str = request.args.get('money')
    if not money_str:
        return jsonify({'error': 'Missing money parameter'}), 400

    try:
        global balance
        balance = float(money_str)
    except ValueError:
        return jsonify({'error': 'Invalid money format'}), 400

    return jsonify({'success': True})

if __name__ == '__main__':
    app.run(debug=True)

