from flask import Flask, request, jsonify, Response, stream_with_context
from threading import Thread
from time import time, sleep
import csv
import dnstwist
import json

class DNSTwistAPI(Flask):
    def __init__(self, name):
        super().__init__(name)
        self.sessions = []
        self._setup_routes()
        self.janitor_thread = Thread(target=self.janitor, daemon=True)
        self.janitor_thread.start()

    def _setup_routes(self):
        self.add_url_rule('/', 'root', self.root)
        self.add_url_rule('/api/scan', 'api_scan', self.api_scan, methods=['POST'])
        self.add_url_rule('/api/status/<sid>', 'api_status', self.api_status)
        self.add_url_rule('/api/domains/<sid>', 'api_domains', self.api_domains)
        self.add_url_rule('/api/csv/<sid>', 'api_csv', self.api_csv)
        self.add_url_rule('/api/json/<sid>', 'api_json', self.api_json)
        self.add_url_rule('/api/list/<sid>', 'api_list', self.api_list)
        self.add_url_rule('/api/stop/<sid>', 'api_stop', self.api_stop)

    def root(self):
        return "DNSTwist API - Domain Permutation and Scanning Service"

    def api_scan(self):
        domain = request.json.get('domain')
        if not domain:
            return jsonify({'error': 'Domain parameter is required'}), 400

        sid = str(time()) + '-' + domain.replace('.', '_')
        session = {
            'id': sid,
            'status': 'processing',
            'start_time': time(),
            'results': [],
            'domain': domain,
            'timeout': 300
        }
        self.sessions.append(session)

        def scan_domains():
            try:
                permutations = dnstwist.generate_permutations(domain)
                for perm in permutations:
                    result = dnstwist.check_domain(perm)
                    if result['registered']:
                        session['results'].append(result)
            finally:
                session['status'] = 'completed'

        Thread(target=scan_domains).start()
        return jsonify({'session_id': sid}), 201

    def api_status(self, sid):
        session = next((s for s in self.sessions if s['id'] == sid), None)
        if not session:
            return jsonify({'error': 'Session not found'}), 404

        return jsonify({
            'id': session['id'],
            'status': session['status'],
            'progress': len(session['results']) / len(dnstwist.generate_permutations(session['domain'])) * 100 if session['status'] == 'processing' else 100,
            'created_at': session['start_time'],
            'domain': session['domain']
        })

    def api_domains(self, sid):
        session = next((s for s in self.sessions if s['id'] == sid), None)
        if not session:
            return jsonify({'error': 'Session not found'}), 404

        if session['status'] == 'processing':
            return jsonify({'error': 'Scan still in progress'}), 409

        return jsonify({'domains': session['results']})

    def api_csv(self, sid):
        session = next((s for s in self.sessions if s['id'] == sid), None)
        if not session:
            return 'Session not found', 404, {'Content-Type': 'text/plain'}

        if session['status'] == 'processing':
            return 'Scan still in progress', 409, {'Content-Type': 'text/plain'}

        csv_data = []
        csv_data.append(['Domain', 'Registered', 'Registrar', 'Creation Date', 'Expiration Date', 'Last Updated'])
        for result in session['results']:
            csv_data.append([
                result['domain'],
                result['registered'],
                result.get('registrar', ''),
                result.get('creation_date', ''),
                result.get('expiration_date', ''),
                result.get('last_updated', '')
            ])

        output = StringIO()
        writer = csv.writer(output)
        writer.writerows(csv_data)
        return output.getvalue(), 200, {'Content-Type': 'text/csv'}

    def api_json(self, sid):
        session = next((s for s in self.sessions if s['id'] == sid), None)
        if not session:
            return jsonify({'error': 'Session not found'}), 404

        if session['status'] == 'processing':
            return jsonify({'error': 'Scan still in progress'}), 409

        return jsonify({'results': session['results']})

    def api_list(self, sid):
        session = next((s for s in self.sessions if s['id'] == sid), None)
        if not session:
            return 'Session not found', 404, {'Content-Type': 'text/plain'}

        if session['status'] == 'processing':
            return 'Scan still in progress', 409, {'Content-Type': 'text/plain'}

        output = '\n'.join([f"{result['domain']} ({result['registered']})" for result in session['results']])
        return output, 200, {'Content-Type': 'text/plain'}

    def api_stop(self, sid):
        session = next((s for s in self.sessions if s['id'] == sid), None)
        if not session:
            return jsonify({'error': 'Session not found'}), 404

        if session['status'] == 'completed':
            return jsonify({'error': 'Scan already completed'}), 409

        session['status'] = 'stopped'
        return jsonify({'status': 'stopped'})

    def janitor(self):
        while True:
            now = time()
            for session in self.sessions[:]:
                if session['status'] == 'completed' or now - session['start_time'] > session['timeout']:
                    self.sessions.remove(session)
            sleep(60)

    def run(self, host='0.0.0.0', port=5000):
        super().run(host=host, port=port)

