            reader = csv.DictReader(csv_file)
            texts = [row['text'] for row in reader]
        else:
            texts = request.data.decode('utf-8').splitlines()

        predictions = []
        for text in texts:
            pred = score_text(text)
            predictions.append(pred)

        output = StringIO()
        writer = csv.writer(output)
        writer.writerow(['text', 'predictions'])
        for text, pred in zip(texts, predictions):
            pred_str = ','.join(pred)
            writer.writerow([text, pred_str])

        return Response(output.getvalue(), mimetype='text/csv'), 200

    else:
        return jsonify({"error": "Unsupported content type"}), 415

