        def index():
            if request.method == 'POST':
                document = request.form['review']
                return self.results(document)
            return self.index()

        @self.app.route('/results', methods=['POST'])
        def results():
            document = request.form['review']
            label, prob = self.classify(document)
            review_id = self.sqlite_entry(document, label)
            return self.results_page(document, label, prob, review_id)

        @self.app.route('/feedback', methods=['POST'])
        def feedback():
            document = request.form['review']
            user_label = int(request.form['feedback'])
            self.update_feedback(document, user_label)
            return redirect(url_for('index'))

    def classify(self, document: str) -> tuple[str, float]:
        X = self.vectorizer.transform([document])
        prediction = self.clf.predict(X)
        probability = self.clf.predict_proba(X).max()
        return prediction[0], probability

    def train(self, document: str, y: int) -> None:
        X = self.vectorizer.transform([document])
        self.clf.partial_fit(X, [y])

    def sqlite_entry(self, document: str, y: int) -> int:
        conn = sqlite3.connect(self.db)
        c = conn.cursor()
        c.execute('''CREATE TABLE IF NOT EXISTS reviews
                     (id INTEGER PRIMARY KEY, text TEXT, sentiment INTEGER, feedback INTEGER)''')
        c.execute("INSERT INTO reviews (text, sentiment) VALUES (?, ?)", (document, y))
        conn.commit()
        id = c.lastrowid
        conn.close()
        return id

    def index(self):
        return render_template('index.html')

    def results_page(self, document, label, prob, review_id):
        return render_template('results.html', document=document, label=label, probability=prob, review_id=review_id)

    def update_feedback(self, document, user_label):
        conn = sqlite3.connect(self.db)
        c = conn.cursor()
        c.execute("UPDATE reviews SET feedback = ? WHERE text = ?", (user_label, document))
        conn.commit()
        conn.close()
        self.train(document, user_label)

    def run(self, debug: bool = True) -> None:
        self.app.run(debug=debug)

