        self.config = {
            'DATABASE_URL': os.getenv('DATABASE_URL')
        }
        self.db = db
        self._setup_routes()
        self.db.init_app(self.app)

    def _setup_routes(self):
        self.app.add_route(self.exchange_rates, '/exchange_rates', methods=['GET'])

    async def exchange_rates(self, request: Request) -> HTTPResponse:
        date_str = request.args.get('date')
        if date_str:
            try:
                date = datetime.strptime(date_str, '%Y-%m-%d').date()
            except ValueError:
                return json({'error': 'Invalid date format'}, status=400)
        else:
            latest = await ExchangeRates.query.order_by(ExchangeRates.date.desc()).gino.first()
            if not latest:
                return json({'error': 'No exchange rates available'}, status=404)
            date = latest.date

        rates_record = await ExchangeRates.query.filter(ExchangeRates.date == date).gino.first()
        if not rates_record:
            return json({'error': 'No exchange rates for the specified date'}, status=404)

        base = request.args.get('base')
        symbols = request.args.get('symbols')

        original_base = 'EUR'
        converted_rates = rates_record.rates.copy()

        if base and base != original_base:
            base_rate = converted_rates.get(base)
            if not base_rate:
                return json({'error': f'Base currency {base} not found'}, status=400)
            converted_rates = {k: v / base_rate for k, v in converted_rates.items()}

        if symbols:
            symbol_list = symbols.split(',')
            filtered_rates = {k: v for k, v in converted_rates.items() if k in symbol_list}
            missing = [s for s in symbol_list if s not in converted_rates]
            if missing:
                return json({'error': f'Symbols {", ".join(missing)} not found'}, status=400)
            converted_rates = filtered_rates

        return json({
            'base': base or original_base,
            'date': date.isoformat(),
            'rates': converted_rates
        }, status=200)

    def run(self, host: str = "0.0.0.0", port: int = 8000) -> None:
        self.app.run(host=host, port=port)

