from sanic import Sanic, Request, HTTPResponse, json
from collections import defaultdict
import os

class FamiliaAPI:
    def __init__(self, model_path: str, model_name: str, n_workers: int = None):
        self.app = Sanic(__name__)
        self.model_name = model_name
        self.model_path = model_path
        self.n_workers = n_workers
        self.model_dir = os.path.join(model_path, model_name)
        self.emb_file = "word_embeddings.bin"
        self.inference_engine_lda = InferenceEngineWrapper(self.model_dir, "lda")
        self.inference_engine_slda = InferenceEngineWrapper(self.model_dir, "slda")
        self.twe = TopicalWordEmbeddingsWrapper(self.model_dir, self.emb_file)
        self.lda_topic_words = self._read_topic_words_from_file()
        self._setup_routes()

    def _read_topic_words_from_file(self, topic_words_file_name: str = 'topic_words.lda.txt') -> defaultdict:
        topic_words = defaultdict(list)
        file_path = os.path.join(self.model_dir, topic_words_file_name)
        with open(file_path, 'r') as f:
            for line in f:
                parts = line.strip().split('\t')
                if len(parts) >= 3:
                    topic_id, word, score = parts[0], parts[1], float(parts[2])
                    topic_words[topic_id].append((word, score))
        return topic_words

    def _get_param(self, request: Request, param_name: str, default_value=None, is_list=False) -> Union[str, List[str]]:
        data = request.json or {}
        value = data.get(param_name, default_value)
        if is_list and isinstance(value, str):
            return [v.strip() for v in value.split(',')]
        return value

    def _strip_to_none(self, text: str) -> Optional[str]:
        stripped = text.strip()
        return stripped if stripped else None

    def _response(self, success: bool = True, data=None, message=None) -> HTTPResponse:
        return json({"success": success, "data": data, "message": message})

    def _error_response(self, message: str = 'Invalid request') -> HTTPResponse:
        return self._response(success=False, message=message)

    def _setup_routes(self):
        @self.app.route('/tokenize', methods=['POST'])
        async def tokenize(request: Request) -> HTTPResponse:
            text = self._get_param(request, 'text')
            cleaned_text = self._strip_to_none(text)
            if not cleaned_text:
                return self._error_response('Empty text provided')
            tokens = self.twe.tokenize(cleaned_text)
            return self._response(data={"tokens": tokens})

        @self.app.route('/infer', methods=['POST'])
        async def infer(request: Request) -> HTTPResponse:
            text = self._get_param(request, 'text')
            model_type = self._get_param(request, 'model_type', 'lda')
            cleaned_text = self._strip_to_none(text)
            if not cleaned_text:
                return self._error_response('Empty text provided')
            if model_type == 'lda':
                topics = self.inference_engine_lda.predict([cleaned_text])
            elif model_type == 'slda':
                topics = self.inference_engine_slda.predict([cleaned_text])
            else:
                return self._error_response('Unsupported model type')
            return self._response(data={"topics": topics})

    def run(self):
        self.app.run(host="0.0.0.0", port=8000, workers=self.n_workers)

