                         r'(?P<pronounciation>.*) </s>', re.MULTILINE)

    def __init__(self, fst_model=None, nbest=None):
        self._logger = logging.getLogger(__name__)
        self.fst_model = os.path.abspath(fst_model)
        self.nbest = nbest

    def execute(self, input, is_file=False):
        cmd = ['phonetisaurus-g2p',
               '--model=%s' % self.fst_model,
               '--input=%s' % input,
               '--words']

        if is_file:
            cmd.append('--isfile')

        if self.nbest is not None:
            cmd.extend(['--nbest=%d' % self.nbest])

        cmd = [str(x) for x in cmd]
        try:
            proc = subprocess.Popen(cmd, stdout=subprocess.PIPE,
                                    stderr=subprocess.PIPE)
            stdoutdata, stderrdata = proc.communicate()
        except OSError:
            self._logger.error("Error occured while executing command '%s'",
                             ' '.join(cmd), exc_info=True)
            raise

        if stderrdata:
            for line in stderrdata.splitlines():
                message = line.strip()
                if message:
                    self._logger.debug(message)

        if proc.returncode != 0:
            self._logger.error("Command '%s' return with exit status %d",
                             ' '.join(cmd), proc.returncode)
            raise OSError("Command execution failed")

        result = {}
        if stdoutdata is not None:
            for word, precision, pronounc in self.PATTERN.findall(stdoutdata):
                if word not in result:
                    result[word] = []
                result[word].append(pronounc)
        return result

